import React, { useEffect, useState } from 'react';
import Swal from 'sweetalert2';
import Axios from "axios";
import $ from 'jquery';

import Cart from '../Components/Cart';

const LOCAL_STORAGE_KEY = 'menu.orderData';

function Menu() {
    const [backendData, setBackendData] = useState([{}]);
    const [orderData, setOrderData] = useState({});

    const Toast = Swal.mixin({
        toast: true, position: 'top-end', showConfirmButton: false, timer: 3000, timerProgressBar: true,
        didOpen: (toast) => {
            toast.addEventListener('mouseenter', Swal.stopTimer);
            toast.addEventListener('mouseleave', Swal.resumeTimer);
        }
    })

    const UpdateCart = (newList) => {
        console.log('Updating Cart')
        console.log(newList)
        if(newList) {
            console.log('New List')
            console.log(newList)
            localStorage.setItem(LOCAL_STORAGE_KEY, JSON.stringify(newList));
        }

        if(!orderData || Object.entries(orderData).length == 0) $('#cart').hide()
        else $('#cart').show()

    }

    const AddToCart = ({id, name}) => {
        console.log(`Add to Cart - ${id} ${name}`);
        // Setup Data 
        let newList = orderData;
        // Add or Increment
        newList[id] = (typeof newList[id] === 'undefined') ? 1 : newList[id] + 1;
        // Set New Data
        setOrderData(newList);

        // Update Cart Storage and Visual
        UpdateCart();

        // Notify the User 
        Toast.fire({ icon: 'success', titleText: `1 ${name} Added to Cart` });
    }

    const RemoveFromCart = ({id, name}) => {
        console.log(`Remove From Cart - ${id} ${name}`);
        // Setup Data 
        let newList = orderData;
        // Remove or Decrement
        if(newList[id] > 1) {
            console.log('Decrement ' + newList[id]);
            newList[id]--;
        } else {
            console.log('Remove ' + id);
            delete newList[id];
        }
        console.log(newList);
        // Set New Data
        setOrderData(newList);

        // Update Front-End Visuals
        UpdateCart(newList);

        // Notify the User 
        Toast.fire({ icon: 'success', titleText: `1 ${name} Removed From Cart` });
    }

    // Submit Cart 
    const SubmitOrder = () => {
        Swal.fire({
            title: 'Add Order',
            customClass: 'swal-dark',
            html: `<input type="text" id="name" class="swal2-input" placeholder="Name">`,
            confirmButtonText: 'Create Order',
            showCancelButton: true,
            focusConfirm: false,
            preConfirm: () => {
                const name = Swal.getPopup().querySelector('#name').value
                if (!name) { Swal.showValidationMessage(`Please Enter A Name For this Order`); }
                return { name: name };
            }
        }).then((result) => {
            let data = {
                name: result.value.name,
                products: orderData
            }
            console.log('Data:');
            console.log(data);

            let notify = (title = 'Success', icon = 'success') => { Swal.fire({titleText: title, customClass: 'swal-dark', icon: icon}); };
            let error = () => { notify('Failed', 'error') };

            console.log('Attempting to POST');
            Axios.post('/api', data).then(response => {
                if(response.data.success) {
                    notify();
                } else {
                    error();
                }

            }).catch(error => {
                console.log(error);
                error();
            });
            // BARTENDER.Helpers.ajax('AddOrder', data, successAction, 'Order Creation');
        });
    }

    useEffect(() => {
        UpdateCart();
    }, [orderData])

    // Load Products Initially 
    useEffect(() => {
        // Get Product Data 
        fetch('/api').then( response => response.json()
        ).then( data => { setBackendData(data) });

        // Get Order Data
        const local = localStorage.getItem(LOCAL_STORAGE_KEY);
        if(typeof local !== "undefined") {
            try {
                const newOrderData = JSON.parse(local);
                if(newOrderData) setOrderData(newOrderData);
                console.log(!newOrderData);
            } catch (e) {
                console.log('Failed To Parse');
                console.log(local);
            }
        }
        UpdateCart();
    }, []);

    return (
        <div className="card text-bg-dark m-2 border-none">
            <div className="card-header text-center">
                <h1> Menu </h1>
            </div>

            {(typeof backendData.products === 'undefined') ? (
            <div className="card-body">
                <div className='card-header'>
                    <h2> Loading Menu...  </h2>
                </div>
            </div>
            ) : (
            <div className="card-body">
                <div>
                    <ul className="list-group mb-3">
                        {backendData.products.map(({ id, name, description, price }) => 
                            <li className="list-group-item list-group-item-dark list-group-item-action" key={id} id={`${id}ProductItem`}>
                                <div className="d-flex w-100 justify-content-between">
                                    <h5 className="mb-1">{id}. {name}</h5>
                                    <small>${price}ea</small>
                                </div>
                                <div className="mb-1 ps-5">
                                    <div className="d-flex w-100 justify-content-between">
                                        <div>{description}</div>
                                    <small className='pe-3'> 
                                        <i onClick={e => AddToCart({id, name})} className='fa fa-plus text-success hover-spin'></i> 
                                    </small>
                                    </div>
                                </div>
                            </li>
                        )}
                    </ul>
                </div>

                <div id='cart'>
                    <div className='card-header'>
                        <h3> Cart </h3>
                    </div>
                    <table className="table table-dark table-striped table-hover mb-3">
                        <thead>
                            <tr>
                                <th scope='col'>Name</th>
                                <th scope='col' className='text-center'>Price</th>
                                <th scope='col' className='text-center'>Quantity</th>
                                <th scope='col' className='text-center'>SubTotal</th>
                                <th scope='col' className='text-center'>Remove</th>
                            </tr>
                        </thead>
                        <tbody>
                            {(typeof orderData !== 'undefined' && Object.entries(orderData).length > 0) ? (
                                <Cart products={backendData.products} cart={orderData} handler={RemoveFromCart} />
                            ) : ( 
                                <tr>
                                    <td className='text-center' colSpan={5}>Your Cart is Currently Empty</td>
                                </tr>
                            )}
                        </tbody>
                    </table>
                    <div className="btn btn-outline-success d-none w-100" onClick={e => SubmitOrder()} id="submitOrderBtn">Submit Order</div>
                </div>
            </div>
            )}
        </div>
    )
}

export default Menu
