import React, { useEffect, useState } from 'react';
import Axios from "axios";
import Swal from 'sweetalert2';
import $ from "jquery";

function Menu() {
    const [backendData, setBackendData] = useState([{}]);
    const [orderData, setOrderData] = useState({});

    const Toast = Swal.mixin({
        toast: true, position: 'top-end', showConfirmButton: false, timer: 3000, timerProgressBar: true,
        didOpen: (toast) => {
            toast.addEventListener('mouseenter', Swal.stopTimer);
            toast.addEventListener('mouseleave', Swal.resumeTimer);
        }
    })
    
    const RenderCart = () => {
        console.log('Render Cart Start');
        console.log(orderData);

        const cartHeader = () => `
            <li class="list-group-item">
                <div class="d-flex w-100 justify-content-between">
                    <h5 class="mb-1">Name</h5>
                    <div>Quantity</div>
                    <div>Price (ea)</div>
                    <div>Remove</div>
                </div>
            </li>`;
        
        const cartItem = ({id, name, price}, quantity) => { return `
            <li class="list-group-item list-group-item-secondart" id='${id}ProductItem'>
                <div class="d-flex w-100 justify-content-between">
                    <h5 class="mb-1">${name}</h5>
                    <div>${quantity}</div>
                    <div>$ ${price} ea</div>
                    <div><small><i class='fa fa-minus text-danger hover-spin'></i></small></div>
                </div>
            </li>`};

        if(Object.entries(orderData).length > 0) {
            let contents = `${cartHeader()}`;
            Object.keys(orderData).forEach(key => {
                let order = orderData[key];
                console.log('Order' + JSON.stringify(order));
                const product = backendData.products.find(prod => prod.id = key);
                console.log('Products' + JSON.stringify(backendData.products));
                console.log('Product' + JSON.stringify(product));
                console.log('Count ' + order);
                contents += cartItem(product, order)
            });
            // console.log('Render: ' + contents);
            $('#cartContents').html(contents);

            // Show Content
            $('#cart').show();
            $(`#submitOrderBtn`).removeClass('d-none');
        } else {
            // Hide Content
            $('#cart').hide();
            $(`#submitOrderBtn`).addClass('d-none');
        }
        console.log('Render Cart Stop');
    }

    const AddToCart = ({id, name}) => {
        console.log(`Add to Cart - ${id} ${name}`);
        // Setup Data 
        let newList = orderData;
        // Add or Increment
        newList[id] = (typeof newList[id] === 'undefined') ? 1 : newList[id] + 1;
        // Set New Data
        setOrderData(newList);

        // Update Front-End Visuals
        RenderCart();

        // Notify the User 
        Toast.fire({ icon: 'success', titleText: `1 ${name} Added to Cart` });
    }

    // Submit Cart 
    const SubmitOrder = () => {
        Swal.fire({
            title: 'Add Order',
            customClass: 'swal-dark',
            html: `<input type="text" id="name" class="swal2-input" placeholder="Name">`,
            confirmButtonText: 'Create Order',
            showCancelButton: true,
            focusConfirm: false,
            preConfirm: () => {
                const name = Swal.getPopup().querySelector('#name').value
                if (!name) { Swal.showValidationMessage(`Please Enter A Name For this Order`); }
                return { name: name };
            }
        }).then((result) => {
            let data = {
                name: result.value.name,
                products: orderData
            }
            console.log('Data:');
            console.log(data);

            let notify = (title = 'Success', icon = 'success') => { Swal.fire({titleText: title, customClass: 'swal-dark', icon: icon}); };
            let error = () => { notify('Failed', 'error') };

            console.log('Attempting to POST');
            Axios.post('/api', data).then(response => {
                if(response.data.success) {
                    notify();
                } else {
                    error();
                }

            }).catch(error => {
                console.log(error);
                error();
            });
            // BARTENDER.Helpers.ajax('AddOrder', data, successAction, 'Order Creation');
        });
    }

    // Load Products Initially 
    useEffect(() => {
        // RenderCart();
        fetch('/api').then(
            response => response.json()
        ).then(
            data => {
                setBackendData(data)
            }
        )
    }, []);

    return (
        <div className="card text-bg-dark m-2 border-none">
            <div className="card-header text-center">
                <h1> Menu </h1>
            </div>

            <div className="card-body">
                {(typeof backendData.products === 'undefined') ? (
                <div className='card-header'>
                    <h2> Loading Menu...  </h2>
                </div>
                ) : (
                <div>
                    <ul className="list-group mb-3">
                        {backendData.products.map(({ id, name, description, price }) => 
                            <li className="list-group-item list-group-item-dark list-group-item-action" key={id} id={`${id}ProductItem`}>
                                <div className="d-flex w-100 justify-content-between">
                                    <h5 className="mb-1">{id}. {name}</h5>
                                    <small>${price}ea</small>
                                </div>
                                <div className="mb-1 ps-5">
                                    <div className="d-flex w-100 justify-content-between">
                                        <div>{description}</div>
                                    <small className='pe-3'> 
                                        <i onClick={e => AddToCart({id, name})} className='fa fa-plus text-success hover-spin'></i> 
                                    </small>
                                    </div>
                                </div>
                            </li>
                        )}
                    </ul>
                </div>
                )}

                <div id='cart' onLoad={$('#cart').hide()}>
                    <div className='card-header'>
                        <h3> Cart </h3>
                    </div>
                    <ul className="list-group mb-3" id='cartContents'>
                        <li className="list-group-item">
                            <div className="d-flex w-100 justify-content-between">
                                <h5 className="mb-1">Name</h5>
                                <div>Quantity</div>
                                <div>Price (ea)</div>
                                <div>Remove</div>
                            </div>
                        </li>
                    </ul>
                    <div className="btn btn-outline-success d-none w-100" onClick={e => SubmitOrder()} id="submitOrderBtn">Submit Order</div>
                </div>
            </div>
        </div>
    )
}

export default Menu
