import React from 'react';
import { Routes, Route, Navigate } from "react-router-dom";
import Dashboard from "../Pages/Dashboard";
// import Register from '../Pages/Register';
import Login from '../Pages/Login';
import Menu from '../Pages/Menu';

export default class RouteController extends React.Component {
    render() {
        console.log('token:' + this.props.token_id);
        const token = sessionStorage.getItem(this.props.token_id);
        console.log('token:' + token);
        // if(token) {
        //     console.log(token);
        //     return <Navigate to="/dashboard" replace={true} />
        //     // TODO: SETUP REDIRECT
        //     // REF: https://www.digitalocean.com/community/tutorials/how-to-add-login-authentication-to-react-applications 
        // }
        return (
            <div className='container'>
            <Routes>
                <Route path="/" element={<Menu />} />
                <Route path="/login" element={<Login setToken={this.props.setToken} />} />
                {/* <Route path="/register" element={<Register />} /> */}
                <Route path="/dashboard" element={<Dashboard />} />
            </Routes>
            </div>
        );
    }
}
