import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { Navigate } from 'react-router-dom';
import Swal from 'sweetalert2';

async function loginUser(credentials) {
  return fetch('/api/user', 
  { method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify(credentials) }
  ).then(data => data.json())
}

export default function Login({ getToken, setToken }) {
  const [username, setUserName] = useState();
  const [password, setPassword] = useState();
  
  if(getToken()) {
    return (
      <Navigate to="/dashboard" />
    )
  }

  const handleSubmit = async e => {
    e.preventDefault();
    const token = await loginUser({
      username, password
    });
    if(token.success) {
      setToken(token.data);
      window.location.pathname = "dashboard";
    } else {
      Swal.fire({title: "Login Failed", icon: 'error', customClass: 'swal-dark'})
    }
  }

  return(
    <div className="card text-bg-dark m-2 border-none">
      <div className="card-header text-center">
        <h1>Please Log In</h1>
      </div>
      <div className="card-body">
        <form className="row" onSubmit={handleSubmit}>
          <div className="col-12 form-group">
            <label for="u">Username</label>
            <input name="u" autoComplete="off" type="text" onChange={e => setUserName(e.target.value)} />
          </div>
          <label>
            <p>Password</p>
            <input name="p" autoComplete="off" type="password" onChange={e => setPassword(e.target.value)} />
          </label>
          <div>
            <button type="submit">Submit</button>
          </div>
        </form>
      </div>
    </div>
  )
}

Login.propTypes = {
  setToken: PropTypes.func.isRequired
};
