import React, { useEffect, useState } from 'react';

function Menu() {
    const [backendData, setBackendData] = useState([{}]);

    useEffect(() => {
        fetch('/api').then(
            response => response.json()
        ).then(
            data => {
                setBackendData(data)
            }
        )
    }, []);

    return (
        <div class="card text-bg-dark m-2">
            <div class="card-header text-center">
                <h1> Menu </h1>
            </div>

            {(typeof backendData.products === 'undefined') ? (
                <div class="card-body">
                    <p>Loading... </p>
                </div>
            ) : (
                <div class="card-body">
                    <input type="hidden" name="order" value="" />

                    <ul className="list-group list-group-flush mb-3">
                        {backendData.products.map(product => MenuProduct(product))}
                    </ul>
                    <div className="btn btn-outline-success d-none w-100" onClick="BARTENDER.Order.SubmitCart()" id="submitOrderBtn">Submit Order</div>
                </div>
            )}
        </div>
    )
}

function MenuProduct ({ id, name, description, price }) {
    return (
        <li className="list-group-item list-group-item-dark list-group-item-action" id={id} onClick="BARTENDER.Order.AddToCart({id})">
            <div className="d-flex w-100 justify-content-between">
                <h5 className="mb-1">{id}. {name}</h5>
                <small>${price}ea</small>
            </div>
            <p className="mb-1 ps-5">{description}</p>
        </li>
    );
}

export default Menu
