import React, { useEffect, useState } from 'react';
import Swal from 'sweetalert2';

function Menu() {
    const [backendData, setBackendData] = useState([{}]);
    const [orderData, setOrderData] = useState([]);

    useEffect(() => {
        fetch('/api').then(
            response => response.json()
        ).then(
            data => {
                setBackendData(data)
            }
        )
    }, []);

    const ToggleCart = id => {
        // Setup Data 
        const curElement = document.getElementById(`${id}ProductItem`);
        const orderElement = document.getElementById(`order`);
        const submitElement = document.getElementById(`submitOrderBtn`);
        let newList = orderData;

        // Either Add or Remove
        if(orderData.includes(id)) {
            Swal.fire('It Works')
            // Remove From List
            newList.splice(orderData.indexOf(id), 1);
            curElement.classList.remove('list-group-item-success');
            curElement.classList.add('list-group-item-dark');
        } else {
            // Add to List 
            newList = [id, ...orderData]
            curElement.classList.remove('list-group-item-dark');
            curElement.classList.add('list-group-item-success');
        }
        // Set New Data
        setOrderData(newList);

        // Show/Hide Submit Button 
        orderElement.value = JSON.stringify(newList);
        if(orderElement.value.length != 2) {
            submitElement.classList.remove('d-none');
        } else {
            submitElement.classList.add('d-none');
        }
    };

    return (
        <div className="card text-bg-dark m-2">
            <div className="card-header text-center">
                <h1> Menu </h1>
            </div>

            {(typeof backendData.products === 'undefined') ? (
                <div className="card-body">
                    <p>Loading... </p>
                </div>
            ) : (
                <div className="card-body">
                    <input type="hidden" name="order" id="order" value={orderData} onChange={e => console.log(this.value)} />

                    <ul className="list-group mb-3">
                        {backendData.products.map(({ id, name, description, price }) => 
                            <li className="list-group-item list-group-item-dark list-group-item-action" key={id} id={`${id}ProductItem`} onClick={e => {ToggleCart(id)}}>
                                <div className="d-flex w-100 justify-content-between">
                                    <h5 className="mb-1">{id}. {name}</h5>
                                    <small>${price}ea</small>
                                </div>
                                <p className="mb-1 ps-5">{description}</p>
                            </li>
                        )}
                    </ul>
                    <div className="btn btn-outline-success d-none w-100" /*onClick="BARTENDER.Order.SubmitCart()"*/ id="submitOrderBtn">Submit Order</div>
                </div>
            )}
        </div>
    )
}

export default Menu
