import React, { useEffect, useState } from 'react';

function Menu() {
    const [backendData, setBackendData] = useState([{}]);
    const [orderData, setOrderData] = useState([]);

    useEffect(() => {
        fetch('/api').then(
            response => response.json()
        ).then(
            data => {
                setBackendData(data)
            }
        )
    }, []);

    const ToggleCart = id => {
        let newList = orderData;
        if(orderData.includes(id)) {
            // Remove From List
            newList.splice(orderData.indexOf(id), 1);
        } else {
            // Add to List 
            newList = [id, ...orderData]
        }
        setOrderData(newList);
        $('input[name="order"]').val(JSON.stringify(newList));
        // console.log(JSON.stringify(newList));
        // console.log(JSON.stringify(orderData));
    };

    return (
        <div className="card text-bg-dark m-2">
            <div className="card-header text-center">
                <h1> Menu </h1>
            </div>

            {(typeof backendData.products === 'undefined') ? (
                <div className="card-body">
                    <p>Loading... </p>
                </div>
            ) : (
                <div className="card-body">
                    <input type="hidden" name="order" value={orderData} onChange={e => console.log(this.value)} />

                    <ul className="list-group mb-3">
                        {backendData.products.map(({ id, name, description, price }) => 
                            <li className="list-group-item list-group-item-dark list-group-item-action" key={id} id={id} onClick={e => {ToggleCart(id)}}>
                                <div className="d-flex w-100 justify-content-between">
                                    <h5 className="mb-1">{id}. {name}</h5>
                                    <small>${price}ea</small>
                                </div>
                                <p className="mb-1 ps-5">{description}</p>
                            </li>
                        )}
                    </ul>
                    <div className="btn btn-outline-success d-none w-100" /*onClick="BARTENDER.Order.SubmitCart()"*/ id="submitOrderBtn">Submit Order</div>
                </div>
            )}
        </div>
    )
}

export default Menu
