import React, { useState, useEffect, useCallback } from 'react'

/**
 * Dashboard Page 
 * @param {Function} param0 Get Logged In Token
 * @returns {React.Component}
 */
function Dashboard({ getToken }) {
    const [ backendData, setBackendData ] = useState({})

    function getOrder (token, id) {
        fetch('/api/orders', { method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify({key: token, id: id}) })
        .then( response => response.json())
        .then( data => { 
            console.log(data)
            if(data.success === true) {
                console.log('Success')
                setBackendData(data.data) 
            } else {
                console.log('Error')
            }
        });
    }

    useEffect(() => {
        console.log('Attempting to Receive Data')
        // Get Orders Data 
        fetch('/api/orders', { method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify({key: getToken()}) })
        .then( response => response.json())
        .then( data => { 
            console.log(data)
            if(data.success === true) {
                console.log('Success')
                setBackendData(data.data) 
            } else {
                console.log('Error')
            }
        });
    }, [])

    return (
        <div className="card text-bg-dark m-2 border-none">
            <div className="card-header text-center">
                <h1>Dashboard</h1>
            </div>
            <div className="card-body text-center">
                {(typeof backendData === 'undefined') ? (
                    <h2>Loading... </h2>
                ):(
                    <div>{(JSON.stringify(backendData))}</div>
                )}
            </div>
        </div>
    )
}

export default Dashboard
