import React, { useEffect, useState } from 'react';

function Menu() {
    const [menuProducts, setMenuProducts] = useEffect([{}]);

    useEffect(() => {
        fetch('/api').then(
            response => response.json()
        ).then(
            data => {
                setMenuProducts(data)
            }
        )
    }, []);

    return (
        <div>
            Menu
            {menuProducts}
        </div>
    )
}

function MenuProduct ({ id, name, description, price }) {
    return (
        <li class="list-group-item list-group-item-action" id="{id}" onclick="BARTENDER.Order.AddToCart({id})">
            <div class="d-flex w-100 justify-content-between">
                <h5 class="mb-1">{id}. {name}</h5>
                <small>${price}ea</small>
            </div>
            <p class="mb-1 ps-5">{description}</p>
        </li>
    );
}

export default Menu
