import React, { useEffect, useState } from 'react';
import Axios from "axios";
import Swal from 'sweetalert2';
import $ from "jquery";

function Menu() {
    const [backendData, setBackendData] = useState([{}]);
    const [orderData, setOrderData] = useState([]);

    // Load Products Initially 
    useEffect(() => {
        Axios.get('/api').then(
            response => {setBackendData(response)}
        )
    }, []);

    // Toggle Item in Cart 
    const ToggleCart = id => {
        // Setup Data 
        const curElement = $(`#${id}ProductItem`);
        const orderElement = $(`#order`);
        const submitElement = $(`#submitOrderBtn`);
        let newList = orderData;

        // Either Add or Remove
        if(orderData.includes(id)) {
            // Remove From List
            newList.splice(orderData.indexOf(id), 1);
            curElement.removeClass('list-group-item-success').addClass('list-group-item-dark');
        } else {
            // Add to List 
            newList = [id, ...orderData]
            curElement.removeClass('list-group-item-dark').addClass('list-group-item-success');
        }
        // Set New Data
        setOrderData(newList);

        // Show/Hide Submit Button 
        orderElement.val(JSON.stringify(newList));
        if(orderElement.val() && orderElement.val().length != 2) {
            submitElement.removeClass('d-none');
        } else {
            submitElement.addClass('d-none');
        }
    };

    // Submit Cart 
    const SubmitOrder = () => {
        Swal.fire({
            title: 'Add Order',
            customClass: 'swal-dark',
            html: `<input type="text" id="name" class="swal2-input" placeholder="Name">`,
            confirmButtonText: 'Create Order',
            showCancelButton: true,
            focusConfirm: false,
            preConfirm: () => {
                const name = Swal.getPopup().querySelector('#name').value
                if (!name) { Swal.showValidationMessage(`Please Enter A Name For this Order`); }
                return { name: name };
            }
        }).then((result) => {
            let data = {
                name: result.value.name,
                products: $('input[name="order"]').val()
            }
            
            console.log(data);

            let successAction = (data) => {
                // BARTENDER.Helpers.Swal.Reload.success('Order Submitted');
            };

            // Axios.post('/api')
            // BARTENDER.Helpers.ajax('AddOrder', data, successAction, 'Order Creation');
        });
    }

    return (
        <div className="card text-bg-dark m-2">
            <div className="card-header text-center">
                <h1> Menu </h1>
            </div>

            {(typeof backendData.products === 'undefined') ? (
                <div className="card-body">
                    <p>Loading... </p>
                </div>
            ) : (
                <div className="card-body">
                    <input type="hidden" name="order" id="order" value={orderData} onChange={e => console.log(this.value)} />

                    <ul className="list-group mb-3">
                        {backendData.products.map(({ id, name, description, price }) => 
                            <li className="list-group-item list-group-item-dark list-group-item-action" key={id} id={`${id}ProductItem`} onClick={e => {ToggleCart(id)}}>
                                <div className="d-flex w-100 justify-content-between">
                                    <h5 className="mb-1">{id}. {name}</h5>
                                    <small>${price}ea</small>
                                </div>
                                <p className="mb-1 ps-5">{description}</p>
                            </li>
                        )}
                    </ul>
                    <div className="btn btn-outline-success d-none w-100" onClick={e => SubmitOrder()} id="submitOrderBtn">Submit Order</div>
                </div>
            )}
        </div>
    )
}

export default Menu
