import React from 'react';
import NavBar from './Components/NavBar';
import RouteController from "./Controller/RouteController";

const LOCAL_STORAGE_KEY = 'bartender.user';

function setToken(userToken) {
    sessionStorage.setItem(LOCAL_STORAGE_KEY, JSON.stringify(userToken));
}

function getToken() {
    sessionStorage.getItem(LOCAL_STORAGE_KEY);
}

function App() {
  const token_id = LOCAL_STORAGE_KEY;

  return (
    <div className='container'>
      <NavBar token_id={token_id} />
      <div className='card text-bg-dark m-2'>
        <RouteController token_id={token_id} getToken={getToken} setToken={setToken} />
      </div>
    </div>
  )
}

export default App
